import React from "react";
import { Container, Row, Col } from "react-bootstrap";
import ContactForm from "./ContactForm";



const Contact = () => {
  const fadeIn = {
    animation: "fadeIn 1s ease-in",
  };

  return (
    <>
      <Container className="py-4 mt-2">
        <Row className="justify-content-center my-4 ">
          <Col md={6}>
            <div className="text-center animate-slide-up" >
              <h3>
                Take the First Step Towards{" "}
                <span style={{ color: "skyblue" }}> Hassle-Free</span>{" "}
                Technology
              </h3>
            </div>

           <div className="bg-light py-2 px-3 my-3" style={{border:'1px solid skyblue',borderRadius:'10px'}}>
            <h3 className="small my-3">Phone</h3>
            <p className="small">+91 6381475573</p>
            <h3 className="small my-3">Headquarters</h3>
            <p className="small">Tirupattur, Vellore, Tamil Nadu</p>
            <h3 className="small my-3">Specialties</h3>
            <p className="small"> Mobile, Web, Desktop, Web Design, QA, and Testing</p>
            </div>
            {/* <p className="my-5 text-center">
              Fill out the form to schedule a complimentary consultation and
              learn how we can help you achieve your technology goals.
            </p> */}
            <h3 className="my-3">What to Expect During Your Consultation
            </h3>
            <ul>
              <p><li>A personalized, one-on-one discussion with our technology expert.</li></p>
              <p><li>A thorough review of your current technology infrastructure and goals.</li></p>
              <p><li>A Q&A session to address any questions or concerns you may have.</li></p>
              <p><li>A tailored presentation outlining our recommended solutions and strategies.</li></p>
            </ul>
          </Col>

          <Col md={6}>
            {/* <ContactForm /> */}
                  <Container>
            <div className="form-container">
              <Form onSubmit={handleSubmit}>
                {/* Input Fields */}
                {[
                  { label: "First Name", name: "firstName", required: true },
                  { label: "Last Name", name: "lastName", required: true },
                  { label: "Email", name: "email", required: true },
                ].map(({ label, name, required }, index) => (
                  <Form.Group controlId={name} key={index} className="mb-3">
                    <Form.Label style={{ color: "blue" }}>
                      {label} {required && <span style={{ color: "red" }}>*</span>}
                    </Form.Label>
                    <Form.Control
                      type="text"
                      name={name}
                      value={formData[name]}
                      onChange={handleChange}
                      placeholder={`Enter ${label.toLowerCase()}`}
                      style={{
                        borderColor: errors[name] ? "red" : "skyblue",
                        borderWidth: "2px",
                      }}
                    />
                    {errors[name] && (
                      <Form.Text className="text-danger">{errors[name]}</Form.Text>
                    )}
                  </Form.Group>
                ))}

                {/* Password Field with Eye Icon */}
                <Form.Group controlId="password" className="mb-3">
                  <Form.Label style={{ color: "blue" }}>
                    Password <span style={{ color: "red" }}>*</span>
                  </Form.Label>
                  <div style={{ position: "relative" }}>
                    <Form.Control
                      type={showPassword ? "text" : "password"}
                      name="password"
                      value={formData.password}
                      onChange={handleChange}
                      placeholder="Enter password"
                      style={{
                        borderColor: errors.password ? "red" : "skyblue",
                        borderWidth: "2px",
                        paddingRight: "40px", // Extra space for the icon
                      }}
                    />
                    <i
                      className={`bi ${showPassword ? "bi-eye-fill" : "bi-eye-slash-fill"}`}
                      onClick={() => setShowPassword(!showPassword)}
                      style={{
                        position: "absolute",
                        top: "50%",
                        right: "10px",
                        transform: "translateY(-50%)",
                        cursor: "pointer",
                        fontSize: "1.2rem",
                        color: "gray",
                      }}
                      title={showPassword ? "Hide Password" : "Show Password"}
                    />
                  </div>
                  {errors.password && (
                    <Form.Text className="text-danger">{errors.password}</Form.Text>
                  )}
                </Form.Group>

                {/* Message Field */}
                <Form.Group controlId="message" className="mb-3">
                  <Form.Label style={{ color: "blue" }}>
                    Message (Optional)
                  </Form.Label>
                  <Form.Control
                    as="textarea"
                    rows={3}
                    name="message"
                    value={formData.message}
                    onChange={handleChange}
                    placeholder="Enter your message"
                    style={{
                      borderColor: errors.message ? "red" : "skyblue",
                      borderWidth: "2px",
                    }}
                  />
                  {errors.message && (
                    <Form.Text className="text-danger">{errors.message}</Form.Text>
                  )}
                </Form.Group>

                {/* "I am not a robot" Section */}
                <Form.Group controlId="robotCheck" className="mb-4">
                  <Row className="align-items-center">
                    <Col xs="auto">
                      <Form.Check
                        type="checkbox"
                        name="isRobot"
                        checked={formData.isRobot}
                        onChange={handleChange}
                        className="me-2"
                      />
                    </Col>
                    <Col>
                      <Form.Label className="mb-0">I am not a robot</Form.Label>
                    </Col>
                  </Row>
                  {errors.isRobot && (
                    <Form.Text className="text-danger">{errors.isRobot}</Form.Text>
                  )}
                </Form.Group>

                {/* Submit Button */}
                <Button className="btn btn-primary btn-lg" type="submit">
                  Submit
                </Button>
              </Form>
            </div>
          </Container>
          </Col>
        </Row>
      </Container>
    </>
  );
};

export default Contact;
